function cloneDeep(x) {
	if (Array.isArray(x)) {
		return x.map( (v) => cloneDeep(v) );
	}
	else if (typeof x === 'object') {
		const entries = Object.entries(x);
		const out = {};
		entries.forEach( ([key, value]) => {
			out[key] = cloneDeep(value);
		});
		return out;
	}
	return x;
}


