function rot(x,k) { return ( (x << k) | (x >>> (32-k)) ) >>> 0; }

function ranval( /* ranctx */ x ) {

	const rb = rot(x.b, 27);
  const rc = rot(x.c, 17);
  const e = (x.a - rb) >>> 0;
  x.a = (x.b ^ rc) >>> 0;
  x.b = ((x.c + x.d) & 0xFFFFFFFF) >>> 0;
  x.c = ((x.d + e) & 0xFFFFFFFF) >>> 0;
  x.d = ((e + x.a) & 0xFFFFFFFF) >>> 0;

	// if (x.d < 0) {
	// 	++ negRandom;
	// 	return ranval(x);
	// }
  return x.d;
}

