function ARRAY(l, fn) {
	fn = fn || (() => 0);
	const arr = new Array(l);
	for( let i = 0; i < l; ++i) {
		arr[i] = fn(i);
	}
	return arr;
}

function GRID(w, h, fn) {
	fn = fn || (() => 0);
	const grid = new Array(w);
	for( let i = 0; i < w; ++i ) {
		const row = new Array(h);
		for( let j = 0; j < h; ++j) {
			row[j] = fn(j, i);
		}
		grid[i] = row;
	}
	return grid;
}


function ERROR(message) {
	throw new Error(message);
}


